import React, { Fragment, useState } from 'react'
import { Link, Redirect } from 'react-router-dom';
import { connect } from 'react-redux';
import { setAlert } from '../../actions/alert';
import { register } from '../../actions/auth';
import PropTypes from 'prop-types';



import { JsonWebTokenError } from 'jsonwebtoken';

const Register = ({ setAlert, register, isAuthenticated }) => {

    const [formData, setFormData] = useState(
        {
            name: '',
            email: '',
            password: '',
            password2: ''
        }
    );

    const { name, email, password, password2 } = formData;
    const onChange = e => setFormData(
        { ...formData, [e.target.name]: e.target.value }
    );

    const onSubmit = async e => {
        e.preventDefault();
        if (password != password2) {
            setAlert("Hey ! Ninja, Password Mismatched !", 'danger');
        }
        else {
            register({ name, email, password });
        }

    }

    if (isAuthenticated) {
        return <Redirect to="dashboard" />
    }

    return (
        <Fragment>
            <h1 className="large text-primary">Be a Ninja</h1>
            <p className="lead">  Setup Account  &nbsp; <i className="fas fa-user"></i> </p>
            <form className="form" onSubmit={e => onSubmit(e)}>
                <div className="form-group">
                    <input type="text" placeholder="Ninja Name" name="name" value={name} onChange={e => onChange(e)} />
                </div>
                <div className="form-group">
                    <input type="email" placeholder="Email Address" name="email" value={email} onChange={e => onChange(e)} />
                    <small className="form-text">Use Gravatar email !</small>
                </div>
                <div className="form-group">
                    <input
                        type="password"
                        placeholder="Password"
                        name="password"
                        value={password} onChange={e => onChange(e)}
                    />
                </div>
                <div className="form-group">
                    <input
                        type="password"
                        placeholder="Confirm Password"
                        name="password2"
                        value={password2} onChange={e => onChange(e)}
                    />
                </div>
                <input type="submit" className="btn btn-primary" value="Register" />
            </form>
            <p className="my-1">
                Already a Ninja? <Link to="/login">Sign In</Link>
            </p>
        </Fragment>
    );
}

Register.propTypes = {
    setAlert: PropTypes.func.isRequired,
    register: PropTypes.func.isRequired,
    isAuthenticated: PropTypes.bool,
};

const mapStateToProps = state =>
    ({
        isAuthenticated: state.auth.isAuthenticated

    });
export default connect(mapStateToProps, { setAlert, register })(Register)
